
public class Proifelse10 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char c = '*';
		if (c >= 'a' && c <= 'z') {
			System.out.println("Small letter Alphabet is " + c);
		} else if (c >= 'A' && c <= 'Z') {
			System.out.println("Capital letter Alphabet is " + c);
		} else {
			System.out.println("not an Alphabet " + c);
		}
	}

}
